import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
} from "react-router-dom";
import Signup from "./pages/Signup";
import Login from "./pages/Login";
import UserDashboard from "./pages/UserDashboard";
import OwnerDashboard from "./pages/OwnerDashboard";
import AdminDashboard from "./pages/AdminDashboard";
import ChangePassword from "./components/ChangePassword";
import "./App.css"; // Import CSS

function Navbar({ user, setUser }) {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    setUser(null);
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <div className="nav-logo">Suyog Ratings App</div>
      <div className="nav-links">
        {!user ? (
          <>
            <Link to="/signup">Signup</Link>
            <Link to="/login">Login</Link>
          </>
        ) : (
          <div className="profile-container">
            <img
              src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png"
              alt="Profile"
              className="profile-logo"
              onClick={() => setDropdownOpen(!dropdownOpen)}
            />
            {dropdownOpen && (
              <div className="profile-dropdown animate-fadeIn">
                <p>
                  <b>{user.username}</b>
                </p>
                <ChangePassword userId={user.user_id} />
                <button onClick={handleLogout} className="logout-btn">
                  Logout
                </button>
              </div>
            )}
          </div>
        )}
      </div>
    </nav>
  );
}

function App() {
  const [user, setUser] = useState(null); // user info after login

  return (
    <Router>
      <div className="app-container">
        <Navbar user={user} setUser={setUser} />
        <main className="page-content animate-fadeIn">
          <Routes>
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login setUser={setUser} />} />
            <Route
              path="/user-dashboard"
              element={<UserDashboard user={user} />}
            />
            <Route
              path="/owner-dashboard"
              element={<OwnerDashboard user={user} />}
            />
            <Route
              path="/admin-dashboard"
              element={<AdminDashboard user={user} />}
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
