import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

function Navbar({ user, setUser }) {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [newPassword, setNewPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleLogout = () => {
    setUser(null);
    navigate("/login");
  };

  const handleChangePassword = async () => {
    if (!newPassword) return;
    try {
      await axios.put("http://localhost:8080/change-password", {
        user_id: user.user_id,
        password: newPassword,
      });
      setMessage("Password updated successfully!");
      setNewPassword("");
      setDropdownOpen(false);
    } catch (err) {
      setMessage(err.response?.data?.error || "Error updating password");
    }
  };

  return (
    <nav className="navbar">
      <div className="nav-logo">Suyog Ratings App</div>
      <div className="nav-links">
        {!user ? (
          <>
            <Link to="/signup">Signup</Link>
            <Link to="/login">Login</Link>
          </>
        ) : (
          <div className="profile-container">
            <img
              src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png"
              alt="Profile"
              className="profile-logo"
              onClick={() => setDropdownOpen(!dropdownOpen)}
            />
            {dropdownOpen && (
              <div className="profile-dropdown animate-fadeIn">
                <p>
                  <b>{user.username}</b>
                </p>

                <input
                  type="password"
                  placeholder="New Password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  style={{
                    padding: "6px",
                    borderRadius: "5px",
                    border: "1px solid #ccc",
                  }}
                />
                <button
                  onClick={handleChangePassword}
                  style={{
                    backgroundColor: "#007bff",
                    color: "white",
                    marginTop: "5px",
                    border: "none",
                    padding: "6px 10px",
                    borderRadius: "5px",
                    cursor: "pointer",
                  }}
                >
                  Update Password
                </button>

                <button
                  onClick={handleLogout}
                  style={{
                    backgroundColor: "#dc3545",
                    color: "white",
                    marginTop: "5px",
                    border: "none",
                    padding: "6px 10px",
                    borderRadius: "5px",
                    cursor: "pointer",
                  }}
                >
                  Logout
                </button>

                {message && (
                  <p style={{ color: "green", marginTop: "5px" }}>{message}</p>
                )}
              </div>
            )}
          </div>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
